import React from 'react';
import { Form, Segment, Radio, Card, Label } from 'semantic-ui-react';


const options = [
    { key: '5', text: '5', value: '5' },
    { key: '10', text: '10', value: '10' },
    { key: '15', text: '15', value: '15' },
    { key: '20', text: '20', value: '20' }, 
];

const initialState = {
    breast: '',
    text: '',
    title: ''
};

class BabyForm extends React.Component {

    state = initialState;

    /* This handleChange takes care of all the events, input, textarea, checkbox, select */
    handleChange = (event, name = null, value = null) => {
        name ? console.log(name) : console.log(event.target.name);
        this.setState({ [name ? name : event.target.name]: value ? value : event.target.value })
    };

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);
        this.setState(initialState);
    }

    render() {

        const { breast , text , title } = this.state

        return (
            <Segment    >
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group inline>
                        <label >breast:</label>
                        <Form.Field
                            name = 'breast'
                            control={Radio}
                            label='Right'
                            value='right'
                            checked={breast === 'right'}
                            onChange={(e, {name , value }) => this.handleChange(e, name, value)}
                        />
                        <Form.Field
                            name = 'breast'
                            control={Radio}
                            label='Left'
                            value='left'
                            checked={breast === 'left'}
                            onChange={(e, {name , value }) => this.handleChange(e, name, value)}
                        />
                        <label>duration:</label>
                        <Form.Dropdown
                            name='title'
                            onChange={(event, { name, value }) => this.handleChange(event, name, value)}
                            placeholder='time of breastfeeding...'
                            fluid
                            selection
                            options={options}
                            value={title}
                        />
                    </Form.Group>

                        <Form.TextArea
                            name='text'
                            value={text}
                            onChange={(event, { name, value }) => this.handleChange(event, name, value)}
                            placeholder='Any notes that you want...'
                        />

                    <Form.Button align='right'>Submit</Form.Button>
                </Form>
            </Segment>
        )
    }
}

export default BabyForm;
