import React from 'react';
import { Form, Segment, Radio, Icon, } from 'semantic-ui-react';


const timeOptions = [
    { key: '5', text: '5', value: '5' },
    { key: '10', text: '10', value: '10' },
    { key: '15', text: '15', value: '15' },
    { key: '20', text: '20', value: '20' },
];

const quantityOptions = [
    { key: '5', text: '5', value: '5' },
    { key: '10', text: '10', value: '10' },
    { key: '15', text: '15', value: '15' },
    { key: '20', text: '20', value: '20' },
]

const initialState = {
    breast: '',
    text: '',
    duration: '',
    time: '',
    quantity: ''
};

class BabyForm extends React.Component {

    state = initialState;

    /* This handleChange takes care of all the events, input, textarea, checkbox, select */
    handleChange = (event, name = null, value = null) => {
        name ? console.log(name) : console.log(event.target.name);
        this.setState({ [name ? name : event.target.name]: value ? value : event.target.value })
    };

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);
        this.setState(initialState);
    }

    render() {

        const { breast, text, duration, time, quantity } = this.state

        return (
            <Segment    >
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group inline >
                        <Icon></Icon>
                        <label >side:</label>
                        <Form.Field
                            name='breast'
                            control={Radio}
                            label='Right'
                            value='right'
                            checked={breast === 'right'}
                            width={2}
                            onChange={(e, { name, value }) => this.handleChange(e, name, value)}
                        />
                        <Form.Field
                            name='breast'
                            control={Radio}
                            label='Left'
                            value='left'
                            width={2}
                            checked={breast === 'left'}
                            onChange={(e, { name, value }) => this.handleChange(e, name, value)}
                        />
                        <label>duration:</label>
                        <Form.Dropdown
                            name='duration'
                            onChange={(event, { name, value }) => this.handleChange(event, name, value)}
                            placeholder='time of breastfeeding...'
                            selection
                            width={12}
                            options={timeOptions}
                            value={duration}
                        />
                        <label>time:</label>
                        <Form.Input
                            name='time'
                            type="time"
                            value={time}
                            width={6}
                            onChange={(event, { name, value }) => this.handleChange(event, name, value)}
                        />

                    </Form.Group>
                    <Form.Group>
                        <label>quantity:</label>
                        <Form.Dropdown
                            name='quantity'
                            onChange={(event, { name, value }) => this.handleChange(event, name, value)}
                            placeholder='quantity given..'

                            selection
                            options={quantityOptions}
                            value={quantity}
                        />

                    </Form.Group>
                    <Form.TextArea
                        name='text'
                        value={text}
                        onChange={(event, { name, value }) => this.handleChange(event, name, value)}
                        placeholder='Any notes that you want...'
                    />
                    <Form.Button align='right'>Submit</Form.Button>
                </Form>
            </Segment>
        )
    }
}

export default BabyForm;
