import React from 'react';
import { Form, Input, Dropdown } from 'semantic-ui-react';


/* this will need to be a list of times */
const options = [
    { key: 'm', text: 'Male', value: 'male' },
    { key: 'f', text: 'Female', value: 'female' },
]

class BabyInput extends React.Component {
    state = {}

    handleChange = (e, { value }) => this.setState({ value })

    render() {
        const { value } = this.state
        return (
            <Form>
                <Form.Group widths='equal'>
                    <Form.Field>
                        <label>Duration</label>
                        <Dropdown />
                    </Form.Field>
                    <Form.Field>
                        <label>Time of the day</label>
                        <Dropdown></Dropdown>
                    </Form.Field>
                </Form.Group>


                <Form.Group widths='equal'>

                    <Form.Group inline>
                        <label>Brest side?</label>
                        <Form.Radio
                            label='Right'
                            value='rt'
                            checked={value === 'rt'}
                            onChange={this.handleChange}
                        />
                        <Form.Radio
                            label='Left'
                            value='lt'
                            checked={value === 'lt'}
                            onChange={this.handleChange}
                        />
                    </Form.Group>

                    <Form.TextArea placeholder='The place where you keep your notes...' />
                </Form.Group>

                <Form.Group inline>

                </Form.Group>


                <Form.Button>Save</Form.Button>
            </Form>
        )
    }
}

export default BabyInput;
