import React, { Component } from 'react';
import '../App.css';
import 'semantic-ui-css/semantic.min.css'
import Card from './Card'
import Title from './Title'
import Output from './Output'
import BabyInput from './Input';




class App extends Component {

  //state of the app that will be for (1)Food intake; (2)Output of the baby; (3)Sleep time of the baby. All states will have a different structre
  state = {
    food: {
      title: 'Food',
      data: [{
        date: new Date(Date.parse('2019/01/10')),
        bb: {
          side: 'left',
          duration: 200,
          time: new Date()
        },
        bt: {
          ml: 100,
          time: new Date()
        }
      }]
    },
    output: {
      title: 'Output'
    },
    sleep: {
      title: 'Sleep'
    },
    type: 'food'
  }

  render() {

    const { type, food, output, sleep } = this.state

    return (
      <div className="App">
        {type === 'food' ? (
          <Card>
            <Title title={food.title}/>
            <BabyInput />
            {/* <Output info={food}/> */}
          </Card>
        ) : type === 'output' ? (
          <Card>
            <Title title={output.title}/>
          </Card>
        ) : (
          <Card>
            <Title title={sleep.title}/>
          </Card>
        )}

      </div>
    );
  }
}

export default App;
