import SearchBar from "../components/SearchBar/searchbar";

const clientId = '33c842c37a0242a5add060b9629e9455';
const redirectUri = 'http://localhost:3000';
let accessToken
let expiresIn = undefined;


const Spotify = {
  getAccessToken() {
    if(accessToken)
      return accessToken;
    else if(window.location.href.match(/access_token=([^&]*)/) && window.location.href.match(/expires_in=([^&]*)/))
    {
      accessToken = window.location.href.match(/access_token=([^&]*)/)[1];
      expiresIn = window.location.href.match(/expires_in=([^&]*)/)[1];
      window.setTimeout(() => accessToken = '', expiresIn*1000);
      window.history.pushState('Access Token', null, '/');
      return accessToken;
      console.log("access token successfully retrieved.");
    }
    else
    {
      let url = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=token&scope=playlist-modify-public&redirect_uri=${redirectUri}`;
      window.location = url;
    }
  }, 


      search(searchterm) {
        const options = {headers: {Authorization: `Bearer ${accessToken}`},}
        const searchUrl = `https://api.spotify.com/v1/search?type=track&q=${searchterm.replace(' ', '%20')}`;
      return fetch (searchUrl, options)
      .then(res => res.json())
      .then(data => {
        console.log(data);
        return data.tracks.items.map(track => {
          return {
            id: track.id,
            name: track.name,
            artist: track.artists[0].name,
            album: track.album.name,
            uri: track.uri
          };
      });
      })
      .catch (err => console.log(err));
    },  

    savePlaylist (playListName, trackURIs) {
      const headers = {Authorization: `Bearer ${accessToken}`};
      const userUrl= 'https://api.spotify.com/v1/me' 
      let userId = undefined;
      let playlistId = undefined;

      fetch (userUrl , {headers: headers})
     .then (response => response.json())
     .then (data => userId = data.id)

     .then(() => { 
     const newPlaylistUrl = `https://api.spotify.com/v1/users/${userId}/playlists`;
     fetch (newPlaylistUrl, {
       method: 'POST',
       headers: headers, 
       body: JSON.stringify({
        playListName: playListName
        }) 
     })
    })
     .then (res => res.json())
     .then (data => playlistId = data.id)

     .then(() => {
      const addPlayListTracksUrl = `https://api.spotify.com/v1/users/${userId}/playlists/${playlistId}/tracks`;
      fetch(addPlayListTracksUrl , {
        method: 'POST',
        headers: headers,
        body: JSON.stringify({
          uris: trackURIs
      })
    });

     })
     },

    }





